INSERT INTO `products` (`product_id`, `category_id`, `name_product`, `title`, `description`, `price`, `screen`, `quantity`, `size`, `weight`, `color`, `image`, `list_image`, `memory`, `os`, `cpu_speed`, `camera_primary`, `battery`, `warranty`, `bluetooth`, `wlan`, `promotion`, `promotion_price`, `start_promotion`, `end_promotion`, `date_created`) VALUES
(1, 1, 'APPLE', 'Iphone 6 32GB (2017)', 'Iphone 6 (2017) mang trên mình thiết kế đẳng cấp với những đường bo tròn, mặt kính mài ôm theo cạnh máy cho cảm giác khi cầm trên tay thoải mái hơn.', 8999000, '', 5, '138.1 x 67 x 6.9 mm', '124 g', 'gray', 'dt1.jpg', '', '32 GB', 'iOS', '1.4 GHz', '1.2 MP', '1810 mAh', '12 tháng', 'Có,V4.0', '4G', 'HOT', 8800000, '2017-08-13', '2017-08-14', '2017-08-31'),
(2, 1, 'APPLE', 'Iphone 7 32GB', 'Dải ăng-ten bị chỉ trích khá nhiều trên dòng Iphone trước đó đã bị xóa bỏ, thay vào đó nó đã được đưa lên sát hai cạnh trên dưới.', 17999000, '', 10, '138.3 x 67.1 x 7.1 mm', '138g', 'Black', 'dt2.jpg', '', '32GB', 'iOS', '2.3 GHz', '7.0 MP', '1960 mAh', '12 tháng', 'v4.2', '4G', 'NEW', 17000000, '2017-08-18', '2017-08-19', '2017-09-03'),
(3, 1, 'APPLE', 'IPhone 7 128GB PRODUCT RED', 'Điểm nhấn ấn tượng nhất của iphone 7 RED là màu đỏ liền mạch đầy cuốn hút.', 19999000, '', 5, '138.3 x 67.1 x 7.1 mm', '138g', 'RED', 'dt3.jpg', '', '128GB', 'iOS', '2.3 GHz', '7.0 MP', '1960 mAh', '12 tháng', 'Bluetooth v4.', '4G', 'HOT', 19000000, '2017-08-14', '2017-08-15', '2017-09-03'),
(4, 1, 'SAMSUNG', 'Samsung Galaxy S8 Plus', 'S8 plus được cập nhật trợ lý ảo mới có tên gọi Bixby.Phần mềm mới này hứa hẹn mang lại nhiều trải nghiệm thú vị', 20490000, ' 6.2 inch Super AMOLED (2560 x 1440 pixel)', 5, '159.5 x 73.4 x 8.1mm', '173g', 'Black', 'dt16.jpg', '', '64 GB', 'Android 7.0', 'Lõi Tám (lõi Tứ 2.3GHz + lõi Tứ 1.7GHz), 64 bit, vi xử lý 10nm', '8.0 MP', '3500 mAh', '12 tháng', 'v5.0', '4G', 'NEW', 20000000, '2017-08-19', '2017-08-20', '2017-09-10'),
(5, 1, 'SAMSUNG', 'Samsung Galaxy A7 (2017)', 'Samsung Galaxy A7 (2017) được thiết kế với phong cách quen thuộc là sự kết hợp giữa khung kính kim loại sang trọng và mặt kính cong 3D cao cấp.', 9990000, ' 5.5 inch (1440 x 2560 pixels)', 15, '156.8 x 77.6 x 7.9 mm', '169g', 'White', 'dt5.jpg', '', '32 GB', 'Android 6.0 (Marshmallow)', '1.9 GHz', '16.0 MP', '3600 mAh', '12 tháng', 'v4.2, A2DP, LE, EDR', '4G', 'HOT', 9490000, '2017-08-17', '2017-08-18', '2017-09-04'),
(6, 1, 'SAMSUNG', 'Samsung Galaxy Note 5', 'Samsung Galaxy Note 5 vẫn sẽ mang đến cho bạn bộ khung kim loại cứng cáp cùng độ bền ấn tượng.', 7909000, ' 5.5 inch (1440 x 2560 pixels)', 5, '153.2 x 76.1 x 7.6 mm', '171 g', 'White', 'dt6.jpg', '', '32 GB', 'Android 5.1.1 (Lollipop)', 'Octa-core (2.1 GHz + 1.5 GHz)', '5.0 MP', '3000 mAh', '12 tháng', 'v4.1, A2DP, EDR, LE', '4G', 'NEW', 6999000, '2017-08-09', '2017-08-15', '2017-09-10'),
(7, 7, 'DELL', 'Dell Vostro V5468/i3-7100U/Dos', 'Dell vẫn trang bị đầy đủ cho Vostro V5468 i3 các cổng giao tiếp chuyên dùng nhất hiện nay.', 12990000, '15.6 inchFull HD', 10, '340 x 240 x 18.5 mm', '1,66 kg', 'Gold', 'mt17.jpg', '', '500GB HDD', 'Free DOS', '2.4 GHz', '5.0 MP', '4 Cell', '12 tháng', '	Bluetooth v4.1', '10/100Mbps', 'HOT', 11990000, '2017-08-24', '2017-08-25', '2017-08-01'),
(8, 7, 'APPLE', 'iPad Wi-Fi 32GB (2017)', 'iPad new 2017 Wi-Fi 32 GB sở hữu một ngoại hình khá giống iPad Air 2, với một thiết kế kim loại cao cấp bao trọn thân máy, lớp vỏ nhôm sắc lạnh được cắt kim cương tỉ mỉ đến từng đường nét nhỏ tạo nên vẻ đẹp vừa sang trọng, vừa mạnh mẽ.', 8990000, 'Retina display, , 9.7 inch(2048 x 1536 pixels)', 5, '240 x 169.5 x 7.5 mm', '469 g', 'black', 'mt8.jpg', '', '32 GB', 'iOS', '1.84 GHz', '1.2 MP', '32.4 Wh', '12 tháng', 'Bluetooth 4.2', 'Wi‑Fi (802.11a/b/g/n/ac); dual band (2.4GHz and 5GHz); HT80 with MIMO', 'NEW', 8490000, '2017-09-07', '2017-09-29', '2017-09-01'),
(9, 12, 'Mobile', 'Usb 3.0 32GB Kingston ', 'Với Usb 3.0 32GB Kingston Data Traveler 100G3 việc lưu trữ và truyền tải dữ liệu sẽ nhanh và dễ dàng hơn bao giờ hết.', 390000, '', 10, '60mm x 21.2mm x10mm', '5 g', 'black', 'pk1.jpg', '', '32 GB', '', '', '', '', '12 tháng', '', '', 'HOT', 349000, '2017-08-24', '2017-08-25', '2017-09-03'),
(10, 1, 'ASUS', 'Asus Zenfone 4 Pro', 'Zenfone 4 Pro tiếp tục thực hiện sứ mệnh mang đến các smartphone giá tốt, cấu hình mạnh nối tiếp sự thành công của Zenfone 3 của nhà Asus, chắc chắn sẽ làm nên cơn sốt trong phân khúc smartphone tầm trung với mục tiêu “ Giá tầm trung, cấu hình tầm cao”.\r\n', 5690000, '5.8 inch (1920 x 1080 pixels)', 11, '138.1 x 67 x 6.9 mm', '138g', 'Gold', 'dt7.jpg', '', '64GB', 'Android OS, v7.1 (Nougat)', '	Qualcomm Snapdragon 835', '	Chính: Dual 12.0 MP + 16.0 MP, Phụ: 8.0 MP', '3000mAh', '12 tháng', '	v4.2, A2DP, EDR, LE', '4G', 'NEW', 5300000, '2017-09-01', '2017-09-04', '2017-08-01'),
(11, 1, 'OPPO', 'OPPO F3 Plus', 'OPPO F3 Plus là một sản phẩm mới được OPPO giới thiệu đến người dùng, máy được đánh giá rất cao về camera kép mặt trước cho hình ảnh chụp sắc nét, sống động. Về ngoại hình, máy vẫn sở hữu thiết kế truyền thống và một cấu hình mạnh mẽ bên trong, cùng nhiều tính năng ấn tượng khác đang chờ người dùng khám phá.', 10690000, ' 6.0 inch (1080 x 1920 pixels)', 12, '	163.63 x 80.8 x 7.35 mm', '163g', 'Gold', 'dt8.jpg', '', '64GB', '	Android 6.0 (Marshmallow)', '	Qualcomm Snapdragon 653', '	Chính: 16.0 MP, Phụ: Dual 16.0 MP + 8.0 MP', '3100mAh', '12 tháng', '	Bluetooth v4.1', '4G', 'HOT', 10400000, '2017-09-01', '2017-09-11', '2017-08-02'),
(12, 1, 'OPPO', 'OPPO F3 ', 'OPPO F3 là biến thể nhỏ gọn hơn của OPPO F3 Plus, nổi bật với camera selfie kép phía trước với 2 cảm biến 16 MP và 8 MP, hỗ trợ chụp ảnh selfie góc rộng rất tốt.', 6990000, '5.5 inch (1920 x 1080 pixels)', 12, '	153.3 x 75.2 x 7.3 mm', '153g', 'Black', 'dt9.jpg', '', '64GB', '	ColorOS 3.0, nền tảng Android 6.0', 'MediaTek MT6750T, 8 Nhân, Octa-core 1.51 Ghz', '	Chính: 13.0 MP, Phụ: Dual 16.0 MP + 8.0 MP', '2800mAh', '12 tháng', '	v4.2, A2DP, LE', '4G', 'HOT', 6600000, '2017-09-01', '2017-09-11', '2017-08-16'),
(14, 1, 'NOKIA', 'Nokia 5', 'Nokia 5 là một mẫu smartphone mới được ra mắt của HDM Global trong năm 2017 này. Sản phẩm sở hữu thiết kế hiện đại và thanh thoát, hiệu suất ổn định, màn hình 5.2 inches với chất lượng hình ảnh tốt, hệ thống camera sắc nét cùng nhiều tính năng ưu việt khác đang chờ bạn khám phá.', 4690000, ' 5.2 inch, 720 x 1280 pixels', 12, '	163.63 x 80.8 x 7.35 mm', '153g', 'Gold', 'dt10.jpg', '', '64GB', '	Android 6.0 (Marshmallow)', '	Qualcomm Snapdragon 653', '	Chính: 16.0 MP, Phụ: Dual 16.0 MP + 8.0 MP', '3400mAh', '12 tháng', '	Bluetooth v4.1', '4G', 'HOT', 4400000, '2017-09-01', '2017-09-11', '2017-08-02'),
(15, 1, 'NOKIA', 'Nokia 3', 'Nokia 3 là một sản phẩm tầm phổ thông mới được HDM Global giới thiệu đến người dùng, với thiết kế thời trang, màn hình 5 inches tiện dụng, hiệu năng ổn định cùng nhiều tính năng khác. Nokia 3 là một smartphone phù hợp cho người dùng có ngân sách hạn chế.a', 2690000, ' 5.2 inch, 720 x 1280 pixels', 12, '	143.4 x 71.4 x 8.48 mm', '143g', 'Gold', 'dt11.jpg', '', '64GB', '	Android 6.0 (Marshmallow)', '	Qualcomm Snapdragon 653', '	Chính: 16.0 MP, Phụ: Dual 16.0 MP + 8.0 MP', '3100mAh', '12 tháng', '	Bluetooth v4.1', '4G', 'SALE', 2400000, '2017-09-01', '2017-09-11', '2017-08-02'),
(16, 1, 'APPLE', 'iPhone 7 Plus 256GB', 'Với những tính năng vượt trội về camera, iPhone 7 Plus red 256GB là sản phẩm nổi bật còn hơn cả iPhone 7 khi cùng ra mắt trước đây, khả năng chụp ảnh zoom 10x với camera kép và chụp chân dung xoá phông đã thuyết phục người dùng một cách ngọt ngào nhất. Không chỉ vậy, với phiên bản màu đỏ rực rỡ này, iPhone 7 Plus thật sự là một sản phẩm đáng mơ ước của nhiều người.', 24999000, '5.5 inch (1920 x 1080 pixels)', 5, '	158.2 x 77.9 x 7.3 mm', '154 g', 'gray', 'dt12.jpg', '', '256 GB', 'iOS', 'Apple A10, 4 Nhân, Quad-core 2.3 GHz', '	Chính: Dual 12.0 MP, Phụ: 7.0 MP', '2810 mAh', '12 tháng', 'Có,V4.0', '4G', 'HOT', 24800000, '2017-08-13', '2017-08-14', '2017-09-06'),
(17, 1, 'APPLE', 'iPhone 5s 16GB', 'iPhone 5S - Thiết kế đã hoàn mỹ lại càng hoàn hảo hơn\r\nĐiều đầu tiên cần nói đến chính là thiết kế. Thông thường các bản nâng cấp của Apple với chữ S đi sau đều giữ nguyên thiết kế bên ngoài mà chủ yếu vẫn là nâng cấp sức mạnh bên trong nhiều hơn. Tuy nhiên, iPhone 5S có thể xem là một ngoại lệ.\r\n \r\nThiết kế iPhone 5S\r\niPhone 5S có một thay đổi đáng ý chú nhất về bề ngoài chính là nút Home', 5999000, '	4 inch (640 x 1136 pixels )', 5, '138.1 x 67 x 6.9 mm', '124 g', 'gray', 'dt13.jpg', '', '16 GB', 'iOS', 'Apple A7, 2 Nhân, Dual-core 1.3Ghz', 'Chính : 8 MP, Phụ 1.2 MP', '1810 mAh', '12 tháng', 'Có,V4.0', '4G', 'HOT', 5400000, '2017-08-13', '2017-08-14', '2017-08-31'),
(18, 1, 'APPLE', 'iPhone 6 Plus 32GB', 'iPhone 6 Plus là một phiên bản iPhone màn hình lớn nhất cao cấp nhất của Apple từ trước đến nay, nhưng nó không chỉ đơn thuần là lớn hơn về kích thước mà còn tốt hơn về mọi mặt. Thiết kế kim loại cao cấp bao phủ màn hình Retina Full HD tuyệt đẹp; mạnh mẽ hơn, nhưng lại tiết kiệm pin hơn; những sự phối hợp giữa phần cứng và phần mềm hoàn hảo, tạo ra một thế hệ mới nhất của iPhone và tốt hơn bất cứ sản phẩm nào trước đây.', 11999000, '5.5 inch, 1080 x 1920 pixels', 10, '	15,81 x 7,78 x 0,71 cm', '158g', 'Black', 'dt14.jpg', '', '32GB', 'iOS', '2.3 GHz', '7.0 MP', '2960 mAh', '12 tháng', 'v4.2', '4G', 'NEW', 9900000, '2017-08-18', '2017-08-19', '2017-09-03'),
(19, 1, 'APPLE', 'iPhone 7 Plus 256GB PRODUCT RED', 'Với những tính năng vượt trội về camera, iPhone 7 Plus red 256GB là sản phẩm nổi bật còn hơn cả iPhone 7 khi cùng ra mắt trước đây, khả năng chụp ảnh zoom 10x với camera kép và chụp chân dung xoá phông đã thuyết phục người dùng một cách ngọt ngào nhất. Không chỉ vậy, với phiên bản màu đỏ rực rỡ này, iPhone 7 Plus đỏ thật sự là một sản phẩm đáng mơ ước của nhiều người', 24999000, '	 5.5 inch (1920 x 1080 pixels)', 5, '	158.2 x 77.9 x 7.3 mm', '154 g', 'RED', 'dt15.jpg', '', '256 GB', 'iOS', 'Apple A10, 4 Nhân, Quad-core 2.3 GHz', '	Chính: Dual 12.0 MP, Phụ: 7.0 MP', '2810 mAh', '12 tháng', 'Có,V4.0', '4G', 'HOT', 24400000, '2017-09-06', '2017-09-21', '2017-09-05'),
(20, 1, 'APPLE', 'Iphone 6 Plus 64GB (2017)', 'Iphone 6 Plus (2017) mang trên mình thiết kế đẳng cấp với những đường bo tròn, mặt kính mài ôm theo cạnh máy cho cảm giác khi cầm trên tay thoải mái hơn.', 9999000, '	5.5 inch (1920 x 1080 pixels)', 5, '138.1 x 67 x 6.9 mm', '124 g', 'gray', 'dt12.jpg', '', '64 GB', 'iOS', 'Apple A10, 4 Nhân, Quad-core 2.3 GHz', 'Chính: Dual 12.0 MP, Phụ: 7.0 MP', '2810 mAh', '12 tháng', 'Có,V4.0', '4G', 'HOT', 9800000, '2017-09-12', '2017-09-21', '2017-09-01'),
