--
-- Cấu trúc bảng cho bảng `promotion`
--

CREATE TABLE `promotion` (
  `id` int(11) NOT NULL,
  `short_title` varchar(100) NOT NULL,
  `short_content` varchar(100) NOT NULL,
  `content` text NOT NULL,
  `image` varchar(50) NOT NULL,
  `date` datetime NOT NULL,
  `author` varchar(50) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `promotion`
--

INSERT INTO `promotion` (`id`, `short_title`, `short_content`, `content`, `image`, `date`, `author`) VALUES
(1, 'Đón Táo 2017 tại FPT Shop ', 'Nhanh tay sắm smartphone', '<p style=\"text-align: justify;\"><strong>F-Plus là gì?</strong></p>\r\n\r\n<p style=\"text-align: justify;\">F-Plus là thiết bị nhỏ gọn, được  FPT Shop cho ra đời với mục tiêu mang đến cho khách hàng những trải nghiệm dịch vụ thú vị, mang đẳng cấp 5 sao với sự chăm sóc VIP nhất, có thể nói là “tận răng” ! Với mục tiêu 3 không: không cần chờ, không cần nhớ, không cần tìm, F-Plus chính là “trợ lý thông minh” phục vụ đặc biệt cho những khách hàng VIP của FPT Shop.</p>\r\n\r\n<ul>\r\n    <li style=\"text-align: justify;\">Thứ nhất, bạn được nhận sự hỗ trợ nhanh nhất từ FPT Shop. Chỉ cần nhấn giữ nhẹ nút đen được trang bị sẵn trên F-Plus trong 3 giây, FPT Shop sẽ phản hồi lại trong vòng 5 phút, ngay cả khi đang ở vị trí không có kết nối Wifi lẫn 3G/4G.</li>\r\n    <li style=\"text-align: justify;\">Thứ hai, F-Plus sẽ giúp bạn nhanh chóng xác định được vị trí chiếc điện thoại của mình bằng cách nhấn vào nút đen trên F-Plus. Chỉ với 1 lần bấm, điện thoại của bạn sẽ phản hồi lại âm thanh giúp bạn dễ dàng tìm được.</li>\r\n    <li style=\"text-align: justify;\">Thứ ba, gắn F-Plus vào vật dụng cá nhân quan trọng, bạn sẽ nhanh chóng tìm thấy khi vô tình để đâu đó. Việc bạn cần làm là vào ứng dụng F-Plus trên smartphone, bấm chọn tính năng tìm đồ, lúc này thiết bị sẽ vang lên để bạn xác định đồ cần tìm nằm ở đâu.</li>\r\n    <li style=\"text-align: justify;\">Thứ tư,  không cần gậy tự sướng, bạn vẫn dễ dàng có được những bức ảnh selfie “chuẩn đẹp” với 1 lần bấm vào nút tròn nhỏ trên F-Plus.</li>\r\n</ul>\r\n\r\n<p> </p>\r\n\r\n<p style=\"text-align: justify;\"><img alt=\"\" src=\"http://localhost:3030/DO_AN/views/imager/news1.jpg\" style=\"width: 800px; height: 818px;\" /></p>\r\n\r\n<p style=\"margin-left: 22.8pt; text-align: justify;\"> </p>\r\n\r\n<p style=\"margin-left: 22.8pt; text-align: justify;\"><span style=\"text-align: justify;\">Ngoài Siêu đặc quyền F-Plus, tất cả iFan đăng ký nhận trước thông tin Táo 2017 ngay từ hôm nay tại FPT Shop còn được mua trả góp 0% qua thẻ tín dụng của các ngân hàng: VP bank, VIB, TPBank, Techcombank, Sacombank, Maritime bank, HSBC, Citibank, ANZ, Shinhan bank, Standard Chatered. Đồngthời, bạn còn có ngay cơ hội trúng 8 giải 80 triệu đồng và 880 giải 4 triệu đồng. </span></p>\r\n\r\n', 'news1.1.jpg', '2017-09-19 14:21:32', 'No Name'),
(2, 'Giảm 15% khi mua galaxy', 'Miễn phí dán màn hình', 'Đùa đấy làm gì mà có chuyện giảm', 'news2.jpg', '2017-09-19 06:15:34', 'No name'),
(3, 'Trả góp không lãi suất', 'Nhanh tay sắm smartphone', 'Cứ mua đi còn lâu mới có trả góp 0%.Làm thế t lỗ à!', 'news3.jpg', '2017-09-14 14:17:00', 'Quen'),
(4, 'Khuyến mãi sốc ngày 30/4 -1/5', 'Ăn kem thả ga khi mua iphone 7s red', 'Làm gì mà có khuyến mãi.Mơ ak', 'news4.jpg', '2017-09-18 14:22:33', 'No name'),
(5, 'Trả góp có lãi suất', 'Nhanh tay sắm smartphone đi', 'Cứ mua đi còn lâu mới có trả góp 0%.Làm thế t lỗ à!', 'news3.jpg', '2017-09-14 14:17:00', 'Quen');

