INSERT INTO `support_cus` (`id`, `title`, `content`, `images`, `date`, `time`, `name_user`) VALUES
(1, 'Bạn ơi cho mình hỏi iphone 7 plus 256GB còn hàng những màu nào nhỉ', 'Chào Phan Thành đệ,<br>\r\n                            Máy đang còn hàng màu vàng, hồng, đen bóng, đen nhám, bạc nha bạn.<br>\r\n                            Bạn vui lòng ấn nút MUA NGAY hoặc liên hệ tổng đài miễn phí 04.21092016 tư vấn bán hàng chúng tôi sẽ hỗ trợ bạn nhanh nhất.<br>\r\n                            Cảm ơn bạn đã quan tâm đến sản phẩm nhé.<br> Thân mến!', '', '2017-09-16', '11:14:23', 'Your Name'),
(2, 'Cho mình hỏi IP 7 plus 128GB có được mua trả góp 0% mà trả trước khoảng 7tr ko ạ? Hồ sơ trả góp cần những gì ạ?', 'Chào Hương Phố em,<br>\r\n                            Sản phẩm này được hỗ trợ trả góp 0% lãi suất, trả trước 7 triệu, kỳ hạn 9 tháng, thủ tục cần có CMND + Hộ khẩu là được bạn nhé.<br>\r\n                            Bạn vui lòng ấn nút Mua Ngay để đặt hàng hoặc liên hệ tổng đài tư vấn bán hàng miễn phí 18006601 để được hỗ trợ nhanh nhất nhé.<br> Thân mến!', 'cus1.jpg', '2017-09-17', '11:14:18', 'Quen'),
(3, 'Em mua máy này ở fpt shop mới được gần 2 tháng mà giờ không hiểu sao bị mất cảm ứng, em phải làm sao ạ?', 'Chào Vân Điệp em,<br>\r\n                            Trước tiên QTV thay mặt FPT Shop xin lỗi bạn về sự bất tiện này. Trong trường hợp này, QTV phiền bạn bớt chút thời gian, mang sản phẩm tới cửa hành FPT Shop gần nhất để nhân viên kiểm tra và bảo hành sản phẩm nếu có lỗi sớm bạn nhé. Mong bạn thông cảm.<br>\r\n                            Thân mến!', '', '2017-09-14', '13:22:14', 'Ly Luu'),
(4, 'Cho mình hỏi IP 7 plus 128GB có được mua trả góp 0% mà trả trước khoảng 7tr ko ạ? Hồ sơ trả góp cần những gì ạ?', 'Chào Phan Quân,<br>\r\n                            Sản phẩm này được hỗ trợ trả góp 0% lãi suất, trả trước 7 triệu, kỳ hạn 9 tháng, thủ tục cần có CMND + Hộ khẩu là được bạn nhé.<br>\r\n                            Bạn vui lòng ấn nút Mua Ngay để đặt hàng hoặc liên hệ tổng đài tư vấn bán hàng miễn phí 18006601 để được hỗ trợ nhanh nhất nhé.<br> Thân mến!', 'cus1.jpg', '2017-09-08', '11:14:18', 'Quyên'),
(5, 'Bạn ơi cho mình hỏi iphone 7 plus 256GB còn hàng những màu nào nhỉ', 'Chào Thế Chột Oppa,<br>\r\n                            Máy đang còn hàng màu vàng, hồng, đen bóng, đen nhám, bạc nha bạn.<br>\r\n                            Bạn vui lòng ấn nút MUA NGAY hoặc liên hệ tổng đài miễn phí 04.21092016 tư vấn bán hàng chúng tôi sẽ hỗ trợ bạn nhanh nhất.<br>\r\n                            Cảm ơn bạn đã quan tâm đến sản phẩm nhé.<br> Thân mến!', '', '2017-09-16', '11:14:23', 'Your Name'),
(6, 'Em mua máy này ở fpt shop mới được gần 2 tháng mà giờ không hiểu sao bị mất cảm ứng, em phải làm sao ạ?', 'Chào Khai Sở Khanh,<br>\r\n                            Trước tiên QTV thay mặt FPT Shop xin lỗi bạn về sự bất tiện này. Trong trường hợp này, QTV phiền bạn bớt chút thời gian, mang sản phẩm tới cửa hành FPT Shop gần nhất để nhân viên kiểm tra và bảo hành sản phẩm nếu có lỗi sớm bạn nhé. Mong bạn thông cảm.<br>\r\n                            Thân mến!', '', '2017-09-14', '13:22:14', 'Ly Luu');

